sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var state = current.getValue('state');

	var user_ids = '';

	if (state=='draft'){
		user_ids = current.armodios_manager + ',' + current.u_owner_qhs + ',' + current.u_glide_list_10;
	}

	if (state=='review'){

		user_ids = current.qhs_manager + ',' + current.u_owner_qhs + ',' + current.u_glide_list_10;

		var gr;

		if (current.nomikh_approve){
			gr = new GlideRecord('sys_user_grmember');
			gr.addQuery('group', 'LEG');
			gr.query();

			while (gr.next()){
				user_ids = user_ids + ',' + gr.getValue('user');
			}
		}

		if (current.ca_adm_approve){
			gr = new GlideRecord('sys_user_group');
			gr.addQuery('name', 'CA');
			gr.query();

			if (gr.next()){
				user_ids = user_ids + ',' + gr.getValue('manager');
			}
		}

	}

	if (state=='commentary'){
		user_ids = current.qhs_manager + ',' + current.armodios_manager + ',' + current.u_glide_list_9;
	}

	if (state=='awaiting approval'){
		user_ids = current.qhs_director + ',' + current.armodios_manager + ',' + current.general_director;
	}

	if (state=='approval'){
		if (current.revise_send_to_general_director){
			user_ids = current.qhs_director + ',' + current.general_director;
		}
		else{
			user_ids = current.qhs_director + ',' + current.armodios_manager;
		}
	}

	if (user_ids.length>1){
		var wl = user_ids.split(',');
		var added = [];
		var final_wl = '';
		for (var i=0;i<wl.length;i++){
			var bl=0;
			for (var j=0;j<added.length;j++){
				if (wl[i]==added[i]){
					bl=1;
				}
			}
			if (bl==0){
				final_wl = final_wl + ',' + wl[i];
				added.push(wl[i]);
			}
		}
	}

	current.u_glide_list_1 = final_wl;

})(current, previous);
