sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var old_attachments = current.x_epats_qhs_qhs_attatchments_included;

	old_attachments = old_attachments.split(',');

	if (old_attachments.length>0){
		var gsa = new GlideSysAttachment();

		var att = new GlideRecord('sys_attachment');
		att.addQuery('table_name','sysapproval_approver');
		att.addQuery("table_sys_id",current.sys_id);
		att.query();



		while(att.next()){
			for (var i=0; i<old_attachments.length; i++){
				if (old_attachments[i]==att.sys_id){
					gsa.deleteAttachment(att.sys_id);
				}
			}
		}
	}

	GlideSysAttachment.copy('sysapproval_approver', current.sys_id, 'x_epats_qhs_qhs_policy', current.document_id);

	att = new GlideRecord('sys_attachment');
	att.addQuery('table_name','sysapproval_approver');
	att.addQuery("table_sys_id",current.sys_id);
	att.query();

	while(att.next()){
		gsa.deleteAttachment(att.sys_id);		
	}

	GlideSysAttachment.copy('x_epats_qhs_qhs_policy', current.document_id, 'sysapproval_approver', current.sys_id);

	var new_attachments = '';

	gsa = new GlideSysAttachment();

	att = new GlideRecord('sys_attachment');
	att.addQuery('table_name','sysapproval_approver');
	att.addQuery("table_sys_id",current.sys_id);
	att.query();

	while(att.next()){
		new_attachments = new_attachments + ',' + att.sys_id;
	}

	if (new_attachments.length>0){
		new_attachments = new_attachments.substr(1,new_attachments.length);
	}

	current.x_epats_qhs_qhs_attatchments_included = new_attachments;

})(current, previous);
