sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	if (current.u_owner_qhs != previous.u_owner_qhs){
		var groups = ['AFC','CA','CON','DEV','GM','GMI','HR','ICT','INSEC','LEG','MB','PROC','QHS','STRAT-REG'];

		for (var i=0;i<groups.length;i++){
			var temp_bool = false;
			var g_gr = new GlideRecord('sys_user_group');
			g_gr.addQuery('name',groups[i]);
			g_gr.query();
			if (g_gr.next()){
				var u_gr = new GlideRecord('sys_user_grmember');
				u_gr.addQuery('user',current.u_owner_qhs);
				u_gr.addQuery('group',g_gr.sys_id);
				u_gr.query();

				if (u_gr.next()){
					current.u_owning_group_qhs = groups[i];
					temp_bool = true;
				}

			}
			if (temp_bool){
				break;
			}
		}
	}
	var dieuthinsh = current.u_owning_group_qhs;

	var gr = new GlideRecord('sys_user_group');
	gr.addQuery('name', 'QHS');
	gr.query();

	if (gr.next()){
		current.qhs_director = gr.x_epats_qhs_director;
		current.qhs_manager = gr.manager;
	}

	gr = new GlideRecord('sys_user_group');
	gr.addQuery('name', current.u_owning_group_qhs);
	gr.query();

	if (gr.next()){
		//current.u_owner_qhs = gr.manager;
		current.department_director = gr.manager;
		current.armodios_manager = gr.manager;
		current.owning_group = gr.sys_id;

		if (current.u_owning_group_qhs=='DEV' || current.u_owning_group_qhs=='CON' || current.u_owning_group_qhs=='GMI'){
			current.department_director_thes = gr.x_epats_qhs_director;
		}
		else{
			current.department_director_thes = '';
		}

	}

	gr = new GlideRecord('sys_user_group');
	gr.addQuery('name', 'LEG');
	gr.query();

	if (gr.next()){
		current.leg_group = gr.sys_id;
	}

	gr = new GlideRecord('sys_user_group');
	gr.addQuery('name', 'CA');
	gr.query();

	if (gr.next()){
		current.ca_group = gr.sys_id;
	}

	if (current.is_revise_program){
		current.assigned_to = current.armodios_manager;
		current.u_owner_qhs = current.qhs_manager;
		current.opened_by = current.qhs_manager;
	}
	else{
		if (!current.u_owner_qhs){
			current.u_owner_qhs = current.opened_by;
		}
	}

	gr = new GlideRecord('sys_user');
	gr.addQuery('title', 'ΓΕΝΙΚΟΣ ΔΙΕΥΘΥΝΤΗΣ');
	gr.query();

	if (gr.next()){
		current.general_director = gr.sys_id;
	}


})(current, previous);
