sys_script_client.script:
function onLoad() {	

	if ((g_user.userID != g_form.getValue('qhs_director'))&&(g_user.userID != g_form.getValue('qhs_manager'))){
		g_form.setReadOnly('send_to_qhs_director',true);
		g_form.setReadOnly('end_of_editing',true);
		g_form.setReadOnly('assigned_to',true);
		g_form.setReadOnly('opened_by',true);
		g_form.setReadOnly('u_glide_date_1',true);
		g_form.setReadOnly('u_glide_list_9',true);
		g_form.setReadOnly('u_glide_list_10',true);
		g_form.setReadOnly('u_owning_group_qhs',true);
		g_form.setReadOnly('scheduled_revision',true);
		g_form.setReadOnly('start_revise_date',true);
		g_form.setSectionDisplay('history',false);
		g_form.setReadOnly('release_date',true);

		if (g_form.getValue('state')=='published' || g_form.getValue('state')=='retired'){
			g_form.setSectionDisplay('notes',false);
			g_form.setReadOnly('u_description_qhs',true);
			g_form.setReadOnly('u_owner_qhs',true);
			g_form.setReadOnly('armodios_manager',true);
			g_form.setReadOnly('name',true);
			g_form.setReadOnly('u_glide_list_9',true);
			g_form.setReadOnly('u_glide_list_10',true);
		}

	}

	if (g_form.getValue('is_revise_program')){
		g_form.setReadOnly('u_glide_list_9',false);
		g_form.setReadOnly('u_glide_list_10',false);
	}

}
