sys_script_client.script:
function onLoad() {
	if (g_form.getValue('state')=='approval' && g_form.getValue('is_revise_program')=='false' && g_user.userID==g_form.getValue('qhs_director')){
			g_form.showFieldMsg('final_ok','Σιγουρευτείτε ότι τα αρχεία που έχουν επισυναπτεί, είναι τα τελικά αρχεία προς κοινοποίηση.','error');
	}

	if (g_form.getValue('state')=='review' && g_form.getValue('is_revise_program')=='false' && g_user.userID==g_form.getValue('qhs_manager')){
			g_form.showFieldMsg('send_to_qhs_director','Σιγουρευτείτε ότι τα αρχεία που έχουν επισυναπτεί, είναι τα τελικά αρχεία προς κοινοποίηση.','error');
	}

	if (g_form.getValue('is_revise_program')==false&&((g_form.getValue('state')=='review'||g_form.getValue('state')=='commentary'||g_form.getValue('state')=='approval')&&(g_user.userID==g_form.getValue('qhs_manager')||g_user.userID==g_form.getValue('qhs_director')))){
			g_form.addErrorMessage('Σιγουρευτείτε ότι τα αρχεία που έχουν επισυναπτεί είναι τα τελικά αρχεία προς κοινοποίηση, πριν φτάσει το έγγραφο στο στάδιο της έκδοσης.');
		}

}
