sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var gr = new GlideRecord('sys_attachment');
	gr.addQuery('table_sys_id',current.sys_id);
	gr.query();

	var attachemt_links = '<p>';

	var aoi = current.attachments_of_interest;
	aoi = aoi.split(',');

	while(gr.next()){
		for (var i=0; i<aoi.length; i++){
			if (aoi[i]==gr.sys_id){
				attachemt_links = attachemt_links + '<a href="/sys_attachment.do?sys_id=' + gr.sys_id + '">' + gr.file_name + '[' + gr.sys_created_on + ']</a><br>';
			}
		}
	}

	attachemt_links = attachemt_links + '</p>';

	template.print(attachemt_links);

})(current, template, email, email_action, event);
