sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var gr = new GlideRecord('x_epats_qhs_qhs_policy');
	gr.addQuery('state','published');
	gr.addQuery('is_revise_program',false);
	gr.addQuery('u_owning_group_qhs',current.u_owning_group_qhs);

	gr.query();

	var mail_html = '<p>';
	var baseURL = gs.getProperty('glide.servlet.uri');

	while(gr.next()){
		mail_html = mail_html + "<a href=" + baseURL + gr.getLink() +">" + gr.name + "</a><br>";
	}

	mail_html = mail_html + '</p>';

	template.print(mail_html);

})(current, template, email, email_action, event);
