sys_ui_action.script:
function onClickA(){
	gsftSubmit(null, g_form.getFormElement(), 'qhs_review'); 
}

if(typeof window == 'undefined'){
	server_onClickA();
}


function server_onClickA(){
	var temp_bool = true;

	var cgr = new GlideRecord('x_epats_qhs_qhs_policy');
	cgr.addQuery('kb_article',current.kb_article);
	cgr.query();

	while (cgr.next()){
		if (cgr.state=='draft'||cgr.state=='review'||cgr.state=='commentary'||cgr.state=='awaiting approval'||cgr.state=='approval'){
			temp_bool = false;
		}
	}

	if (temp_bool){
		var gr = new GlideRecord('x_epats_qhs_qhs_policy');

		gr.initialize();
		gr.name = current.name;
		gr.u_string_1 = current.u_string_1;
		gr.assigned_to = gs.getUserID();
		gr.opened_by = gs.getUserID();
		gr.procedure_2 = 'update';
		gr.kb_article = current.kb_article;
		gr.insert();

		GlideSysAttachment.copy('kb_knowledge',current.kb_article,'x_epats_qhs_qhs_policy',gr.sys_id);

		action.setRedirectURL(gr);

		gs.addInfoMessage('Revison Started');
		current.scheduled_revision = false;
		current.update();
	}
	else{
		gs.addErrorMessage('Policy is already under revision!');
		action.setRedirectURL(current);
	}

}
