sysauto_script.script:
var departments = ['AFC','CA','CON','DEV','GMI','HR','ICT','LEG','MB','PROC','QHS','START-REG'];

var gr = new GlideRecord('x_epats_qhs_qhs_policy');
gr.addQuery('is_revise_program',true);
gr.query();

while (gr.next()){
	if (gr.is_revise_program){
		gr.setValue('state','retired');
		gr.setWorkflow(false);
		gr.update();
	}
}

var today = new GlideDate();
var deadline = new GlideDateTime();
deadline.addDaysLocalTime(15);

for (var i=0; i<departments.length;i++){
	gr = new GlideRecord('x_epats_qhs_qhs_policy');
	gr.initialize();
	gr.name = 'ΠΡΟΓΡΑΜΜΑ ΑΝΑΘΕΩΡΗΣΗΣ ΕΣΩΤΕΡΙΚΩΝ ΕΓΓΡΑΦΩΝ ΤΜΗΜΑΤΟΣ ' + departments[i] + ' | ' + today.getMonthNoTZ() + '/' + today.getYearNoTZ();
	gr.state = 'approval';
	gr.is_revise_program = true;
	gr.u_owning_group_qhs = departments[i];
	gr.u_glide_date_1 = deadline;
	gr.procedure_2 = 'ΔΗΜΙΟΥΡΓΙΑ ΠΡΟΓΡΑΜΜΑΤΟΣ';
	gr.update();
}

gs.info('QHS - Update attachments from approvements run successfuly');
