sysauto_script.script:
var gr = new GlideRecord('x_epats_qhs_qhs_policy');
gr.addEncodedQuery('u_glide_date_1ISNOTEMPTY');
gr.query();

var today = new GlideDateTime();

while(gr.next()){
	var gr_date = new GlideDateTime(gr.getValue('u_glide_date_1'));
	var dur = GlideDateTime.subtract(gr_date,today);

	var gr2 = new GlideRecord('sysapproval_approver');
	gr2.addQuery('state', 'requested');
	gr2.addQuery('document_id',gr.sys_id);
	gr2.query();

	if (dur.getDayPart()==-2){
		//2 days earlier
		while(gr2.next()){
			if (gr2.approver!=gr.qhs_manager && gr2.approver!=gr.qhs_director && gr2.approver!=gr.department_director){
				gs.info('QHS EMAIL REMINDER SENT TO: ' + gr2.getDisplayValue('approver'));
				gs.eventQueue('x_epats_qhs.approval.approval_mail',gr2,gr2.approver, 'ΥΠΕΝΘΥΜΙΣΗ');
			}
		}
	}

	if (dur.getDayPart()==2){
		//2 days after
		while(gr2.next()){
			if (gr2.approver!=gr.qhs_manager && gr2.approver!=gr.qhs_director && gr2.approver!=gr.department_director){
				gs.info('QHS EMAIL REMINDER SENT TO: ' + gr2.getDisplayValue('approver'));
				gs.eventQueue('x_epats_qhs.approval.approval_mail',gr2,gr2.approver, 'ΛΗΞΟΥΣΑ ΠΡΟΘΕΣΜΙΑ');
			}
		}
	}

}
