sysauto_script.script:
var departments = ['AFC','CA','CON','DEV','GM','GMI','HR','ICT','LEG','MB','PROC','QHS','START-REG'];

var today = new GlideDate();
var deadline = new GlideDateTime();
deadline.addDaysLocalTime(15);

for (var i=0; i<departments.length;i++){
	gr = new GlideRecord('x_epats_qhs_qhs_policy');
	gr.initialize();
	gr.name = 'ΕΠΙΠΡΟΣΘΕΤΕΣ ΑΝΑΓΚΕΣ ΠΡΟΓΡΑΜΜΑΤΟΣ ΑΝΑΘΕΩΡΗΣΗΣ ΕΣΩΤΕΡΙΚΩΝ ΕΓΓΡΑΦΩΝ ΤΜΗΜΑΤΟΣ' + departments[i] + ' | ' + today.getMonthNoTZ() + '/' + today.getYearNoTZ();
	gr.state = 'approval';
	gr.is_revise_program = true;
	gr.u_owning_group_qhs = departments[i];
	gr.u_glide_date_1 = deadline;
	gr.procedure_2 = 'ΔΗΜΙΟΥΡΓΙΑ ΠΡΟΓΡΑΜΜΑΤΟΣ';
	gr.update();
}

gs.info('QHS - Update attachments from approvements run successfuly');
