sysevent_script_action.script:
var gsa = new GlideSysAttachment();

var att = new GlideRecord('sys_attachment');
att.addQuery('table_name','sysapproval_approver');
att.addQuery("table_sys_id",current.sys_id);
att.query();

if (att.next()){
	if (att.file_name.includes('.png')){
		gsa.deleteAttachment(att.sys_id);
	}
}

GlideSysAttachment.copy('sysapproval_approver', current.sys_id, 'x_epats_qhs_qhs_policy', current.document_id);

gsa = new GlideSysAttachment();

att = new GlideRecord('sys_attachment');
att.addQuery('table_name','sysapproval_approver');
att.addQuery("table_sys_id",current.sys_id);
att.query();

var text_initial = current.approver.user_id + ' sent the following attachments: ';

var received_attachments = text_initial;

if (att.next()){
	received_attachments = received_attachments + ', ' + att.file_name;
	gsa.deleteAttachment(att.sys_id);
}

while(att.next()){
	received_attachments = received_attachments + ', ' + att.file_name;
	gsa.deleteAttachment(att.sys_id);
}

gs.info('QHS - Update attachments from approvements run successfuly');

if (received_attachments!=text_initial){
	current.comments = received_attachments;
	current.setWorkflow(false);
	current.update();
}
